import React, { useState } from 'react'
import CartContext from './CartContext'


function CartProvider({children}) {

    const [cartItems,setCartItems] = useState ([])
    const [searchBooks,setSearchBooks] = useState("")

    // Add To Cart
    const addToCart = (item) =>{
        const isExist = cartItems.find((cartItem) => cartItem.id === item.id)
        if (isExist) {
            setCartItems(
                cartItems.map((cartItem) => cartItem.id === item.id ? item : cartItem)
            )
        } else {
            setCartItems(prev => [...prev,item])
        }
    }

    // Remove From Cart
    const removeFromCart = (id) => {
        const cart = cartItems.filter (c => c.id !== id)
        setCartItems(cart)
    }

    // Search Book
    const searchBook = (data) => {
        data.filter(book => book.title.toLowerCase().includes(searchBooks))
    }

    // Handle Change Books
    const handleChange = (event) =>{
        setSearchBooks(event.target.value)
    }

  return (
    <CartContext.Provider
     value
     =
        {
            {
                cartItems,
                addToCart,
                removeFromCart,
                cartItemsLength : cartItems.length,
                handleChange,
                searchBook,
                searchBooks
            }
        }
    >

      {children}

    </CartContext.Provider>
  )
}

export default CartProvider
