import React,{useContext, useState} from 'react'
import { Link } from 'react-router-dom'
import CartContext from '../../context/CartContext'
import './Header.css'
import {books} from "../../data/books"

function HeaderTop() {

    const {cartItemsLength} = useContext(CartContext)

    const [search,setSearch] = useState("search")
    const handleClick = () =>{
        search === "search" ? setSearch("search active-search") : setSearch("search")
    }

    const [searchBook,setSearchBook] = useState("")
    const searchedBook = () => {
        let filtered = books.filter(book => book.title.toLowerCase().includes(searchBook))
        console.log('====================================');
        console.log(filtered);
        console.log('====================================');
    }

  return (
    <div className="header-top">
        <Link to="/" className="brand-title">
            <i className="fa-solid fa-book"></i>
            <b className='logo-title'>Bookly</b>
        </Link>

        <form  className={search}>
            <input 
                value={searchBook}
                onChange = {(e) => setSearchBook(e.target.value)}
                type="text" 
                className="inputSearch" 
                placeholder='search here...'
            />

            <Link to="/search" type='button' onClick={searchedBook} className='search-btn' >
                <i className="bi bi-search"></i>
            </Link>
        </form>

        <Link to="/cart" className="add-to-cart">
            {cartItemsLength > 0 && (
                <b className="cart-notification">{cartItemsLength}</b>
            )}
            <i className = "bi bi-cart-fill"></i>
        </Link>
        <div onClick={handleClick} className="toggle-search">
            <i className="fa-solid fa-magnifying-glass"></i>
        </div>
    </div>
  )
}

export default HeaderTop
